def isPrime(n):
    if(n%2==0):
        return False
    for i in range(3,round(sqrt(n))+1,2):
        if n%i == 0:
            return False
    return True

def sqrt(num):
    initGuess = 2
    go = True
    while go:
        nextGuess = .5 * (initGuess + (num/initGuess))
        if abs(nextGuess - initGuess) < 0.001:
            go = False
        initGuess = nextGuess
    return round(nextGuess,3)

def prod(a):
    t = 1
    for i in a:
        t = t * i
    return t

